package com.cn21.preloadlibrary;

import android.app.Fragment;
import android.content.Context;
import android.os.Bundle;
import android.support.annotation.Nullable;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.facebook.react.ReactRootView;

/**
 * Created by lizhj on 2017/7/4.
 */

public abstract class CCCReactFragment extends Fragment {
    private ReactRootView mReactRootView;
    public abstract String getMainComponentName();

    @Override
    public void onAttach(Context context) {
        super.onAttach(context);
        mReactRootView = RNCacheViewManager.getRootView(getMainComponentName());
    }

    @Nullable
    @Override
    public View onCreateView(LayoutInflater inflater, @Nullable ViewGroup container, @Nullable Bundle savedInstanceState) {
        super.onCreateView(inflater, container, savedInstanceState);
        return mReactRootView;
    }

    @Override
    public void onDestroy() {
        RNCacheViewManager.onDestroy(getMainComponentName());
        super.onDestroy();
    }
}
