package com.cn21.preloadlibrary;

import android.app.Activity;
import android.os.Bundle;

import com.facebook.react.ReactActivityDelegate;
import com.facebook.react.devsupport.DoubleTapReloadRecognizer;

import java.lang.reflect.Field;

import javax.annotation.Nullable;

/**
 * Created by lizhj on 2017/7/5.
 */

public class CCCReactFragmentDelegate extends ReactActivityDelegate {

    public CCCReactFragmentDelegate(Activity activity, @Nullable String mainComponentName) {
        super(activity, mainComponentName);
    }

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        Class<ReactActivityDelegate> clazz = ReactActivityDelegate.class;
        try {
            Field field = clazz.getDeclaredField("mDoubleTapReloadRecognizer");
            field.setAccessible(true);
            field.set(this, new DoubleTapReloadRecognizer());
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
